class Solution:
    def characterReplacement(self, s: str, k: int) -> int:
        n = len(s)
        string = s[0]
        longest_str = 0
        start = 0
        reset = True
        k_ = k
        i = 1
        while i < n:
            if s[i] == string[-1]:
                string += s[i]
                i += 1
            elif k_ > 0:
                string += string[-1]
                k_ -= 1
                if reset:
                    start = i
                    reset = False
                i += 1
            else:
                if len(string) + min(k_, n - len(string)) > longest_str:
                    longest_str = len(string) + min(k_, n - len(string))
                if reset:
                    start = i
                reset = True
                string = s[start]
                k_ = k
                i = start + 1
            # print(string, start, i, k_)
        if len(string) + min(k_, n - len(string)) > longest_str:
            longest_str = len(string) + min(k_, n - len(string))
        # print(longest_str)
        return longest_str

if __name__ == '__main__':
    s = Solution()
